package largestnum;

public class LargestNum3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=8055;
		int b=5145;
		int c=a>b?a:b;
		int d=a<b?a:b;
		System.out.println(c);               //for Largest Number
		System.out.println(d);               // for Smallest number
	}

}
